package loop;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int x = 1;
		  
		  
	        while (x <= 4)
	        {
	            System.out.println("Value of x:" + x);
	  
	           
	            
	            x++;
	        }
	}

}
